import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightError,
	InsightResult,
	NotFoundError, ResultTooLargeError
} from "./IInsightFacade";
import Section from "../dataModels/Section";
import JSZip from "jszip";
import CourseData from "../dataModels/CourseData";
import {Parser} from "../query/Parser";
import {Validator} from "../query/Validator";
import {Executor} from "../query/Executor";
/**
 * This is the main programmatic entry point for the project.
 * Method documentation is in IInsightFacade
 *
 */
export default class InsightFacade implements IInsightFacade {
	private datasetCollection: string[] = [];
	private courseDataCollection: CourseData[] = [];
	private queryParser: Parser;
	private queryValidator: Validator;
	private queryExecutor: Executor;
	constructor() {
		console.log("InsightFacadeImpl::init()");
		this.queryParser = new Parser();
		this.queryValidator = new Validator();
		this.queryExecutor = new Executor();
	}

	public async addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		let zipFile = JSZip();
		let info = Array<Promise<string>>();
		let functionPromise: Promise<string[]>;
		let dataset: CourseData;
		return new Promise<string[]>((resolve, reject) => {
			try {
				if (!id || id.includes("_") || id.trim() === "") {
					throw new InsightError("Invalid ID");
				}
				if (this.datasetCollection.includes(id)) {
					throw new InsightError("ID already exists");
				}
				if (kind !== InsightDatasetKind.Sections) {
					throw new InsightError("Invalid Kind Type");
				}
				if (content === null) {
					throw new InsightError("Content should not be empty");
				}
				zipFile.loadAsync(content, {base64: true})
					.then((zip: JSZip) => {
						if (Object.keys(zip.files)[0] !== "courses/") {
							return reject(new InsightError("Folder does not exist in the zip file"));
						}
						const coursesFolder = zip.folder("courses");
						if (!coursesFolder) {
							throw new InsightError("Folder does not exist in the zip file");
						}
						coursesFolder.forEach(function (relativePath, file) {
							info.push(file.async("text"));
						});
						Promise.all(info).then((promise: string[]) => {
							dataset = new CourseData(id, kind, promise);
							if (dataset.sections.length === 0) {
								return reject(new InsightError("No valid sections to add"));
							}
							this.datasetCollection.push(id);
							this.courseDataCollection.push(dataset);
							resolve(this.datasetCollection);
						}).catch((err: any) => {
							reject("Error");
						});
					}).catch((err: any) => {
						reject(new InsightError("Failed to add dataset"));
					});
			} catch (error) {
				reject(error);
			}
		});
	}

	public async removeDataset(id: string): Promise<string> {
		return new Promise<string>((resolve, reject) => {
			try {
				if (!id || id.includes("_") || id.trim() === "") {
					throw new InsightError("Invalid ID");
				}

				const index = this.datasetCollection.indexOf(id);
				if (index === -1) {
					throw new NotFoundError("Dataset not found");
				}

				this.datasetCollection.splice(index, 1);
				this.courseDataCollection.splice(index, 1);

				resolve(id);
			} catch (error) {
				reject(error);
			}
		});
	}

	public async performQuery(query: unknown): Promise<InsightResult[]> {
		const MAX_RESULTS = 5000;
		try {
			// Use QueryParser to parse the query
			const parsedQuery = this.queryParser.parseQuery(query);
			const validatedQuery = this.queryValidator.validateQuery(parsedQuery);

			// Step 2: Retrieve the dataset referenced in the query
			const datasetId = this.queryExecutor.extractDatasetId(parsedQuery);
			const dataset = this.courseDataCollection[this.datasetCollection.indexOf(datasetId)];
			if (!dataset) {
				throw new InsightError("Dataset not found");
			}

			// Step 3: Apply filters defined in the WHERE clause
			const filteredResults = this.queryExecutor.applyFilters(dataset, parsedQuery.WHERE);

			// Step 4: Apply sorting and column selection based on OPTIONS
			const finalResults = this.queryExecutor.formatResults(filteredResults, parsedQuery.OPTIONS);
			// console.log(finalResults.length);
			// Step 5: Check for result size constraints
			if (finalResults.length > MAX_RESULTS) {
				throw new ResultTooLargeError("The query results exceed the allowed limit.");
			}

			return finalResults;
		} catch (error) {
			if (error instanceof ResultTooLargeError) {
				throw new ResultTooLargeError("ll");
			} else {
				throw new InsightError("l");
			}
		}
	}

	public async listDatasets(): Promise<InsightDataset[]> {
		return new Promise<InsightDataset[]>((resolve, reject) => {
			try {
				const datasetList: InsightDataset[] = [];
				for (const courseData of this.courseDataCollection) {
					datasetList.push(courseData.insightDataset);
				}
				resolve(datasetList);
			} catch (error) {
				reject(new InsightError("Failed to list datasets"));
			}
		});
	}
}

/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */

export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export interface InsightResult {
	[key: string]: string | number;
}

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 * working on it right now
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import {InsightDataset, InsightDatasetKind} from "../controller/IInsightFacade";
import Section from "./Section";

export default class CourseData {
	public id: string;
	public insightDatasetKind: InsightDatasetKind;
	public insightDataset: InsightDataset;
	public sections: Section[];

	constructor(id: string, insightDatasetKind: InsightDatasetKind, coursesList: string[]) {
		this.id = id;
		this.insightDatasetKind = insightDatasetKind;
		this.sections = [];
		this.insightDataset = {id: id, kind: insightDatasetKind, numRows: this.sections.length};

		for (let course of coursesList) {
			let parsedCourse = JSON.parse(course);
			this.parse(parsedCourse);
		}
	}

	public parse(courses: any) {
		if (!courses || !courses.result || !Array.isArray(courses.result)) {
			throw new Error("Invalid JSON format");
		}

		for (let course of courses.result) {
			let uuid = course.id;
			let courseID = course.Course;
			let title = course.Title;
			let instructor = course.Professor; // Corrected property name
			let dept = course.Subject;
			let year = course.Year;
			let avg = course.Avg;
			let pass = course.Pass;
			let fail = course.Fail;
			let audit = course.Audit;
			if (course.Section === "overall") { // Corrected property name
				year = 1900;
			}
			let section = new Section(uuid, courseID, title, instructor, dept, year, avg, pass, fail, audit);
			this.sections.push(section);
		}

		this.insightDataset.numRows = this.sections.length;
	}
}

export default class Section {

	private readonly uuid: string;
	private readonly course_id: string;
	private readonly title: string;
	private readonly instructor: string;
	private readonly dept: string;
	private readonly year: number;
	private readonly avg: number;
	private readonly pass: number;
	private readonly fail: number;
	private readonly audit: number;

	constructor(uuid: string, course_id: string, title: string, instructor: string, dept: string,
		year: number, avg: number, pass: number, fail: number, audit: number) {
		this.uuid = uuid;
		this.course_id = course_id;
		this.title = title;
		this.instructor = instructor;
		this.dept = dept;
		this.year = year;
		this.avg = avg;
		this.pass = pass;
		this.fail = fail;
		this.audit = audit;
	}

	public get(keyID: string) {
		if (keyID === "uuid") {
			return this.uuid;
		} else if (keyID === "id") {
			return this.course_id;
		} else if (keyID === "title") {
			return this.title;
		} else if (keyID === "instructor") {
			return this.instructor;
		} else if (keyID === "dept") {
			return this.dept;
		} else if (keyID === "year") {
			return this.year;
		} else if (keyID === "avg") {
			return this.avg;
		} else if (keyID === "pass") {
			return this.pass;
		} else if (keyID === "fail") {
			return this.fail;
		} else if (keyID === "audit") {
			return this.audit;
		}
	}
}

import {InsightError} from "../controller/IInsightFacade";
import {Query, Filter, LogicComparison, MComparison, SComparison, Options} from "../dataModels/Query";
export class Parser {
	public parseQuery(query: unknown): Query {
		// Initial validation of the query object
		if (typeof query !== "object" || query === null) {
			throw new InsightError("Query must be an object");
		}
		// Parse the WHERE and OPTIONS parts of the query
		const whereClause = this.parseWhereClause((query as any)["WHERE"]);
		const optionsClause = this.parseOptionsClause((query as any)["OPTIONS"]);
		// Construct and return the parsed query object
		return {WHERE: whereClause, OPTIONS: optionsClause};
	}

	private parseWhereClause(where: unknown): Filter {
		if (typeof where !== "object" || where === null) {
			throw new Error("WHERE clause must be an object");
		}

		// An empty WHERE object matches all entries
		if (Object.keys(where).length === 0) {
			return {}; // Represents no filter
		}

		const filterType = Object.keys(where)[0];
		const filterValue = (where as any)[filterType];
		switch (filterType) {
			case "AND":
			case "OR":
				return this.parseLogicalComparison(filterType, filterValue);
			case "LT":
			case "GT":
			case "EQ":
				return this.parseMComparison(filterType, filterValue);
			case "IS":
				return this.parseSComparison(filterValue);
			case "NOT":
				return {NOT: this.parseWhereClause(filterValue)};
			default:
				throw new Error(`Invalid filter type: ${filterType}`);
		}
	}

	private parseLogicalComparison(operator: string, filters: unknown): LogicComparison {
		if (!Array.isArray(filters)) {
			throw new Error(`${operator} value must be an array`);
		}
		return {
			[operator]: filters.map((filter) => this.parseWhereClause(filter)),
		};
	}

	private parseMComparison(operator: string, comparison: unknown): MComparison {
		if (typeof comparison !== "object" || comparison === null || Object.keys(comparison).length !== 1) {
			throw new Error(`${operator} comparison requires a single field with a numeric value`);
		}
		const [field, value] = Object.entries(comparison)[0];
		if (typeof value !== "number") {
			throw new Error(`Value for ${field} in ${operator} comparison must be a number`);
		}
		return {[operator]: {[field]: value}};
	}

	private parseSComparison(comparison: unknown): SComparison {
		if (typeof comparison !== "object" || comparison === null || Object.keys(comparison).length !== 1) {
			throw new Error("IS comparison requires a single field with a string value");
		}
		const [field, value] = Object.entries(comparison)[0];
		if (typeof value !== "string") {
			throw new Error(`Value for ${field} in IS comparison must be a string`);
		}
		return {IS: {[field]: value}};
	}

	private parseOptionsClause(options: unknown): Options {
		if (typeof options !== "object" || options === null) {
			throw new Error("OPTIONS must be an object");
		}

		const optionsObj = options as Partial<{COLUMNS: unknown; ORDER: unknown}>;
		if (!Array.isArray(optionsObj.COLUMNS) || optionsObj.COLUMNS.length === 0) {
			throw new Error("COLUMNS must be a non-empty array");
		}

		// Validate that each item in COLUMNS is a string
		const columns = optionsObj.COLUMNS;
		columns.forEach((column) => {
			if (typeof column !== "string") {
				throw new Error("Each item in COLUMNS must be a string");
			}
		});

		const parsedOptions: Options = {
			COLUMNS: columns,
		};

		// If ORDER is specified, validate it
		if (optionsObj.ORDER !== undefined) {
			if (typeof optionsObj.ORDER !== "string") {
				throw new Error("ORDER must be a string");
			}
			parsedOptions.ORDER = optionsObj.ORDER;
		}

		return parsedOptions;
	}
}

import {InsightError} from "../controller/IInsightFacade";
import {Query, Filter, LogicComparison, MComparison, SComparison, Options} from "../dataModels/Query";

export class Validator {
	public validateQuery(query: Query): void {
		this.validateWhereClause(query.WHERE);
		this.validateOptionsClause(query.OPTIONS);
	}

	private validateWhereClause(filter: Filter): void {
		if (Object.keys(filter).length === 0) {
			// An empty WHERE object is valid, matches all entries
			return;
		}

		// Depending on the filter type, validate accordingly
		if ("AND" in filter || "OR" in filter) {
			this.validateLogicComparison(filter as LogicComparison);
		} else if ("LT" in filter || "GT" in filter || "EQ" in filter) {
			this.validateMComparison(filter as MComparison);
		} else if ("IS" in filter) {
			this.validateSComparison(filter as SComparison);
		} else if ("NOT" in filter) {
			this.validateWhereClause((filter as any).NOT);
		} else {
			throw new InsightError("Invalid filter type in WHERE clause");
		}
	}

	private validateLogicComparison(logicComparison: LogicComparison): void {
		const filters = logicComparison.AND || logicComparison.OR;
		if (!Array.isArray(filters) || filters.length === 0) {
			throw new InsightError("AND/OR must have at least one filter");
		}
		filters.forEach((filter) => this.validateWhereClause(filter));
	}

	private validateMComparison(mComparison: MComparison): void {
		const comparator = mComparison.LT || mComparison.GT || mComparison.EQ;
		if (typeof comparator !== "object" || Object.keys(comparator).length !== 1) {
			throw new InsightError("MComparison must have exactly one key-value pair");
		}
		if (typeof Object.values(comparator)[0] !== "number") {
			throw new InsightError("The value for MComparison must be a number");
		}
	}

	private validateSComparison(sComparison: SComparison): void {
		const comparator = sComparison.IS;
		if (typeof comparator !== "object" || Object.keys(comparator).length !== 1) {
			throw new InsightError("SComparison must have exactly one key-value pair");
		}
		if (typeof Object.values(comparator)[0] !== "string") {
			throw new InsightError("The value for SComparison must be a string");
		}
	}

	private validateOptionsClause(options: Options): void {
		if (!options.COLUMNS || options.COLUMNS.length === 0) {
			throw new InsightError("COLUMNS must be a non-empty array");
		}
		options.COLUMNS.forEach((column) => {
			if (typeof column !== "string") {
				throw new InsightError("Each item in COLUMNS must be a string");
			}
		});
		if (options.ORDER && typeof options.ORDER !== "string") {
			throw new InsightError("ORDER must be a string");
		}
	}
}

import {Query, Filter, LogicComparison, MComparison, SComparison, Options, Negation} from "../dataModels/Query";
import CourseData from "../dataModels/CourseData";
import Section from "../dataModels/Section";
import {ResultTooLargeError} from "../controller/IInsightFacade";

export class Executor {
	public extractDatasetId(query: Query): string {
		// Initialize an empty set to collect unique dataset IDs
		let datasetIds = new Set<string>();

		// Extract dataset IDs from the WHERE clause
		this.extractIdsFromFilter(query.WHERE, datasetIds);

		// Extract dataset IDs from the OPTIONS clause
		query.OPTIONS.COLUMNS.forEach((column) => {
			const datasetId = this.extractIdFromKey(column);
			if (datasetId) {
				datasetIds.add(datasetId);
			}
		});

		// Assuming consistency, there should only be one dataset ID
		if (datasetIds.size !== 1) {
			throw new Error("Query must use a single dataset ID");
		}

		return datasetIds.values().next().value;
	}

	private extractIdsFromFilter(filter: Filter, datasetIds: Set<string>): void {
		if (Object.keys(filter).length === 0) {
			// Base case: empty filter
			return;
		}

		Object.entries(filter).forEach(([key, value]) => {
			if (key === "AND" || key === "OR") {
				(value as Filter[]).forEach((subFilter) => this.extractIdsFromFilter(subFilter, datasetIds));
			} else if (key === "NOT") {
				this.extractIdsFromFilter(value as Filter, datasetIds);
			} else {
				// MComparison, SComparison
				const fieldKey = Object.keys(value)[0];
				const datasetId = this.extractIdFromKey(fieldKey);
				if (datasetId) {
					datasetIds.add(datasetId);
				}
			}
		});
	}

	private extractIdFromKey(key: string): string | null {
		const underscoreIndex = key.indexOf("_");
		if (underscoreIndex !== -1) {
			return key.substring(0, underscoreIndex);
		}
		return null;
	}

	public applyFilters(courseData: CourseData, filter: Filter): Section[] {
		if (Object.keys(filter).length === 0) {
			// If the filter is empty, return all sections
			return courseData.sections;
		}

		return courseData.sections.filter((section) => {
			return this.evaluateFilter(section, filter);
		});
	}

	private isLogicComparison(filter: Filter): filter is LogicComparison {
		return "AND" in filter || "OR" in filter;
	}

	private isMComparison(filter: Filter): filter is MComparison {
		return "LT" in filter || "GT" in filter || "EQ" in filter;
	}

	private isSComparison(filter: Filter): filter is SComparison {
		return "IS" in filter;
	}

	private isNegation(filter: Filter): filter is Negation {
		return "NOT" in filter;
	}

	private evaluateFilter(section: Section, filter: Filter): boolean {
		if (this.isLogicComparison(filter)) {
			if (filter.AND) {
				return filter.AND.every((subFilter) => this.evaluateFilter(section, subFilter));
			} else if (filter.OR) {
				return filter.OR.some((subFilter) => this.evaluateFilter(section, subFilter));
			} else {
				return false;
			}
		} else if (this.isNegation(filter)) {
			return !this.evaluateFilter(section, filter.NOT);
		} else if (this.isSComparison(filter)) {
			const [key, comparisonValue] = Object.entries(filter.IS)[0];
			const keyParts = key.split("_");
			const value = filter.IS[key];
			const getValue = section.get(keyParts[1]) as string;
			// Assuming wildcards are not to be implemented in this example
			if (comparisonValue.startsWith("*") && comparisonValue.endsWith("*")) {
				const trimmedValue = comparisonValue.slice(1, -1);
				return getValue.includes(trimmedValue);
			} else if (comparisonValue.startsWith("*")) {
				const trimmedValue = comparisonValue.slice(1);
				return getValue.endsWith(trimmedValue);
			} else if (comparisonValue.endsWith("*")) {
				const trimmedValue = comparisonValue.slice(0, -1);
				return getValue.startsWith(trimmedValue);
			} else {
				return getValue === value;
			}
		} else if (this.isMComparison(filter)) {
			const comparatorKey = Object.keys(filter)[0];
			const key = Object.keys((filter as any)[comparatorKey])[0];
			const targetValue = (filter as any)[comparatorKey][key];
			const keyParts = key.split("_");
			const sectionValue = section.get(keyParts[1]);

			if (sectionValue === undefined) {
				throw new Error("Attempted to compare an undefined value");
			}

			switch (comparatorKey) {
				case "LT": return sectionValue < targetValue;
				case "GT": return sectionValue > targetValue;
				case "EQ": return sectionValue === targetValue;
				default: throw new Error(`Unknown comparison: ${comparatorKey}`);
			}
		} else {
			throw new Error("Unknown filter type");
		}
	}

	public formatResults(sections: Section[], options: Options): any[] {
		// Step 1: Select the specified columns
		const results = sections.map((section) => {
			const result: any = {};
			options.COLUMNS.forEach((column) => {
				const colParts = column.split("_");
				result[column] = section.get(colParts[1]);
			});
			return result;
		});
		if (results.length > 5000) {
			throw new ResultTooLargeError("Oh no");
		}
		// Step 2: Sort the results if an ORDER key is specified
		if (options.ORDER) {
			const orderKey = options.ORDER;
			results.sort((a, b) => {
				if (a[orderKey] < b[orderKey]) {
					return -1;
				} else if (a[orderKey] > b[orderKey]) {
					return 1;
				} else {
					return 0;
				}
			});
		}

		return results;
	}

}
