import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightError,
	InsightResult,
	NotFoundError,
	ResultTooLargeError
} from "./IInsightFacade";
import {Parser} from "../query/Parser";
import {Validator} from "../query/Validator";
import {Executor} from "../query/Executor";
import * as fse from "fs-extra";
import {AddRoomsHelper} from "../util/AddRoomsHelper";
import {AddSectionsHelper} from "../util/AddSectionsHelper";
import {Order} from "../dataModels/Query";

// Adding this comment for Pull Request for reflection (Devin)
// Adding this comment for Pull Request for reflection (Aryaan)

/**
 * This is the main programmatic entry point for the project.
 * Method documentation is in IInsightFacade
 *
 */
export default class InsightFacade implements IInsightFacade {
	public datasetCollection: string[] = [];
	public courseDataCollection: any[] = [];
	private queryParser: Parser;
	private queryValidator: Validator;
	private queryExecutor: Executor;
	private adderRoom: AddRoomsHelper;
	private adderSections: AddSectionsHelper;

	constructor() {
		console.log("InsightFacadeImpl::init()");
		this.queryParser = new Parser();
		this.queryValidator = new Validator();
		this.queryExecutor = new Executor();
		this.adderRoom = new AddRoomsHelper();
		this.adderSections = new AddSectionsHelper();
	}

	public async addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		console.log("START OF ADD");
		if (!id || id.includes("_") || id.trim() === "") {
			throw new InsightError("Invalid ID (contains underscore)");
		}
		if (this.datasetCollection.includes(id)) {
			throw new InsightError("ID already exists");
		}

		if (kind === InsightDatasetKind.Sections) {
			console.log("Sections");
			return this.adderSections.addSectionsDataset(id, content, this.datasetCollection,
				this.courseDataCollection);
		} else if (kind === InsightDatasetKind.Rooms) {
			console.log("Rooms");
			return this.adderRoom.addRoomsDataset(id, content, this.datasetCollection, this.courseDataCollection);
		} else {
			throw new InsightError("Invalid Kind Type");
		}
	}


	public async removeDataset(id: string): Promise<string> {
		const dataDir = "./data/";
		const filePath = `${dataDir}${id}.json`;
		if (!id || id.includes("_") || id.trim() === "") {
			throw new InsightError("Invalid ID");
		}
		try {
			const index = this.datasetCollection.indexOf(id);
			if (index === -1) {
				throw new NotFoundError("Dataset not found");
			}
			// await this.datasetCacheManager.removeDataset(id);

			if (index !== -1) {
				this.datasetCollection.splice(index, 1);
				this.courseDataCollection.splice(index, 1);
			}

			return id;
		} catch (error) {
			if (error instanceof NotFoundError) {
				throw error;
			} else {
				throw new InsightError(`Failed to remove dataset: ${error}`);
			}
		}
	}

	public async performQuery(query: unknown): Promise<InsightResult[]> {
		const MAX_RESULTS = 5000;
		try {
			// Use QueryParser to parse the query
			const parsedQuery = this.queryParser.parseQuery(query);
			// console.log("-----------------------------PARSING START-----------------------------------------");
			console.log(parsedQuery.TRANSFORMATION?.APPLY);
			// console.log("-----------------------------PARSING DONE-----------------------------------------");
			const validatedQuery = this.queryValidator.validateQuery(parsedQuery);
			// console.log("-----------------------------VALIDATOR DONE-----------------------------------------");
			// Step 2: Retrieve the dataset referenced in the query
			const datasetId = this.queryExecutor.extractDatasetId(parsedQuery);
			// console.log("-----------------------------EXTRACT ID-----------------------------------------");
			// console.log(datasetId);
			// console.log("-----------------------------EXTRACT ID DONE-----------------------------------------");
			const dataset = this.courseDataCollection[this.datasetCollection.indexOf(datasetId)];
			if (!dataset) {
				throw new InsightError("Dataset not found");
			}
			// console.log("-----------------------------FOUND DATASET-----------------------------------------");

			// Step 3: Apply filters defined in the WHERE clause
			let filteredResults = this.queryExecutor.applyFilters(dataset, parsedQuery.WHERE);
			// console.log(filteredResults);
			// console.log("-----------------------------APPLIED FILTER START----------------------------------------");
			// console.log("-----------------------------APPLIED FILTER DONE----------------------------------------");
			// Step 4: Apply sorting and column selection based on OPTIONS
			if (parsedQuery.TRANSFORMATION) {
				// console.log("-----------------------------TRANSFORMATION IS THERE----------------------------------");
				filteredResults = this.queryExecutor.executeGroupAndApply(filteredResults, parsedQuery.TRANSFORMATION);
				// console.log("-----------------------------GROUP AND APPLY STARTS------------------------------------");
			}
			// console.log("-----------------------------START OF FORMATTING------------------------------------");
			let finalResults = this.queryExecutor.formatResults(filteredResults, parsedQuery.OPTIONS);
			// Step 5: Check for result size constraints
			if (parsedQuery.OPTIONS.ORDER) {
				const order = parsedQuery.OPTIONS.ORDER;
				// If ORDER is a string, convert it to an Order object with default direction 'UP'
				const orderObject: Order = typeof order === "string" ? {dir: "UP", keys: [order]} : order;
				finalResults = this.queryExecutor.sortResults(finalResults, orderObject);
			}
			if (finalResults.length > MAX_RESULTS) {
				throw new ResultTooLargeError("The query results exceed the allowed limit.");
			}
			// console.log("------------------------------FINAL RESULT STARTS------------------------");
			// console.log(finalResults);
			// console.log("------------------------------FINAL RESULT DONE------------------------");
			return finalResults;
		} catch (error) {
			if (error instanceof ResultTooLargeError) {
				throw new ResultTooLargeError("ll");
			} else {
				throw new InsightError("l");
			}
		}
	}

	public async listDatasets(): Promise<InsightDataset[]> {
		return new Promise<InsightDataset[]>((resolve, reject) => {
			try {
				const datasetList: InsightDataset[] = [];
				for (const courseData of this.courseDataCollection) {
					datasetList.push(courseData.insightDataset);
				}
				resolve(datasetList);
			} catch (error) {
				reject(new InsightError("Failed to list datasets"));
			}
		});
	}
}

/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */

export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export interface InsightResult {
	[key: string]: string | number;
}

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 * working on it right now
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import {InsightError} from "../controller/IInsightFacade";
import {
	Query,
	Filter,
	LogicComparison,
	MComparison,
	SComparison,
	Options,
	ApplyRule,
	Transformations, Order
} from "../dataModels/Query";
export class Parser {

	public parseQuery(query: unknown): Query {
		if (typeof query !== "object" || query === null) {
			throw new InsightError("Query must be an object");
		}

		// Extract WHERE, OPTIONS, and optionally TRANSFORMATIONS from the query
		const whereClause = this.parseWhereClause((query as any)["WHERE"]);
		const optionsClause = this.parseOptionsClause((query as any)["OPTIONS"]);
		const transformationsClause = (query as any)["TRANSFORMATIONS"]
			? this.parseTransformationsClause((query as any)["TRANSFORMATIONS"])
			: undefined; // incase there is no transformation

		// Construct and return the query object
		const parsedQuery: Query = {
			WHERE: whereClause,
			OPTIONS: optionsClause,
		};

		// If TRANSFORMATIONS is present in the query
		if (transformationsClause) {
			parsedQuery.TRANSFORMATION = transformationsClause;
		}

		return parsedQuery;
	}

	public parseWhereClause(where: unknown): Filter {
		if (typeof where !== "object" || where === null) {
			throw new Error("WHERE clause must be an object");
		}

		if (Object.keys(where).length === 0) {
			return {}; // Represents no filter
		}

		const filterType = Object.keys(where)[0];
		const filterValue = (where as any)[filterType];
		switch (filterType) {
			case "AND":
			case "OR":
				return this.parseLogicalComparison(filterType, filterValue);
			case "LT":
			case "GT":
			case "EQ":
				return this.parseMComparison(filterType, filterValue);
			case "IS":
				return this.parseSComparison(filterValue);
			case "NOT":
				return {NOT: this.parseWhereClause(filterValue)};
			default:
				throw new Error(`Invalid filter type: ${filterType}`);
		}
	}

	public parseLogicalComparison(operator: string, filters: unknown): LogicComparison {
		if (!Array.isArray(filters)) {
			throw new Error(`${operator} value must be an array`);
		}
		return {
			[operator]: filters.map((filter) => this.parseWhereClause(filter)),
		};
	}

	public parseMComparison(operator: string, comparison: unknown): MComparison {
		if (typeof comparison !== "object" || comparison === null || Object.keys(comparison).length !== 1) {
			throw new Error(`${operator} comparison requires a single field with a numeric value`);
		}
		const [field, value] = Object.entries(comparison)[0];
		if (typeof value !== "number") {
			throw new Error(`Value for ${field} in ${operator} comparison must be a number`);
		}
		return {[operator]: {[field]: value}};
	}

	public parseSComparison(comparison: unknown): SComparison {
		if (typeof comparison !== "object" || comparison === null || Object.keys(comparison).length !== 1) {
			throw new Error("IS comparison requires a single field with a string value");
		}
		const [field, value] = Object.entries(comparison)[0];
		if (typeof value !== "string") {
			throw new Error(`Value for ${field} in IS comparison must be a string`);
		}
		return {IS: {[field]: value}};
	}

	public parseOptionsClause(options: unknown): Options {
		if (typeof options !== "object" || options === null) {
			throw new InsightError("OPTIONS must be an object");
		}

		const optionsObj = options as Partial<Options>;
		if (!Array.isArray(optionsObj.COLUMNS) || optionsObj.COLUMNS.length === 0) {
			throw new InsightError("COLUMNS must be a non-empty array");
		}

		// Validate that each item in COLUMNS is a string
		const columns = optionsObj.COLUMNS;
		columns.forEach((column) => {
			if (typeof column !== "string") {
				throw new InsightError("Each item in COLUMNS must be a string");
			}
		});

		const parsedOptions: Options = {COLUMNS: columns};

		// If ORDER is specified, validate it
		if (optionsObj.ORDER !== undefined) {
			const order = optionsObj.ORDER;
			if (typeof order === "string") {
				parsedOptions.ORDER = order;
			} else if (typeof order === "object" && order !== null) {
				parsedOptions.ORDER = this.parseOrderClause(order);
			} else {
				throw new InsightError("Invalid ORDER format");
			}
		}

		return parsedOptions;
	}

	private parseOrderClause(order: unknown): Order | string {
		// Check if 'order' is a string, which is a simple case, return it directly
		if (typeof order === "string") {
			return order;
		}

		// If 'order' is not a string, we need to ensure it's an object with the correct properties
		if (typeof order === "object" && order !== null && "dir" in order && "keys" in order) {
			const dir = (order as any).dir;
			const keys = (order as any).keys;
			if (dir !== "UP" && dir !== "DOWN") {
				throw new InsightError("ORDER direction must be UP or DOWN");
			}
			if (!Array.isArray(keys)) {
				throw new InsightError("ORDER keys must be an array");
			}
			keys.forEach((key) => {
				if (typeof key !== "string") {
					throw new InsightError("Each ORDER key must be a string");
				}
			});
			return {dir, keys};
		} else {
			// If 'order' is neither a string nor a properly structured object, throw an error
			throw new InsightError("Invalid ORDER format");
		}
	}

	public parseTransformationsClause(transformations: unknown): Transformations {
		if (typeof transformations !== "object" || transformations === null) {
			throw new InsightError("TRANSFORMATIONS must be an object");
		}

		const transformationsObj = transformations as Partial<Transformations>;
		if (!Array.isArray(transformationsObj.GROUP) || transformationsObj.GROUP.length === 0) {
			throw new InsightError("GROUP must be a non-empty array");
		}

		// Parse GROUP array
		const group: string[] = transformationsObj.GROUP;
		group.forEach((key) => {
			if (typeof key !== "string") {
				throw new InsightError("Each key in GROUP must be a string");
			}
		});

		// Parse APPLY array
		const apply: ApplyRule[] = [];
		if (Array.isArray(transformationsObj.APPLY)) {
			transformationsObj.APPLY.forEach((rule) => {
				const applyKey = Object.keys(rule)[0];
				const applyToken = Object.keys(rule[applyKey])[0];
				const key = rule[applyKey][applyToken];
				if (typeof applyKey !== "string" || typeof applyToken !== "string" || typeof key !== "string") {
					throw new InsightError("Invalid APPLY rule format");
				}
				apply.push({[applyKey]: {[applyToken]: key}});
			});
		}
		apply.forEach((rule: ApplyRule) => {
			const applyKey = Object.keys(rule)[0];
			const applyToken = Object.keys(rule[applyKey])[0];
			const tokenValue = rule[applyKey][applyToken];

			if (!["MAX", "MIN", "AVG", "COUNT", "SUM"].includes(applyToken)) {
				throw new InsightError(`Invalid APPLY token: ${applyToken}`);
			}

			if (typeof tokenValue !== "string") {
				throw new InsightError(`Value for ${applyToken} must be a string`);
			}
		});

		return {
			GROUP: group,
			APPLY: apply
		};
	}
}

import {InsightError} from "../controller/IInsightFacade";
import {
	Query,
	Filter,
	LogicComparison,
	MComparison,
	SComparison,
	Options,
	Order,
	Transformations, ApplyRule
} from "../dataModels/Query";

export class Validator {
	public validateQuery(query: Query): void {
		this.validateWhereClause(query.WHERE);
		this.validateOptionsClause(query.OPTIONS);
		if (query.TRANSFORMATION) {
			this.validateTransformationsClause(query.TRANSFORMATION);
		}
	}

	public validateWhereClause(filter: Filter): void {
		if (Object.keys(filter).length === 0) {
			return;
		}

		// Depending on the filter type, validate accordingly
		if ("AND" in filter || "OR" in filter) {
			this.validateLogicComparison(filter as LogicComparison);
		} else if ("LT" in filter || "GT" in filter || "EQ" in filter) {
			this.validateMComparison(filter as MComparison);
		} else if ("IS" in filter) {
			this.validateSComparison(filter as SComparison);
		} else if ("NOT" in filter) {
			this.validateWhereClause((filter as any).NOT);
		} else {
			throw new InsightError("Invalid filter type in WHERE clause");
		}
	}

	private validateLogicComparison(logicComparison: LogicComparison): void {
		const filters = logicComparison.AND || logicComparison.OR;
		if (!Array.isArray(filters) || filters.length === 0) {
			throw new InsightError("AND/OR must have at least one filter");
		}
		filters.forEach((filter) => this.validateWhereClause(filter));
	}

	private validateMComparison(mComparison: MComparison): void {
		const comparator = mComparison.LT || mComparison.GT || mComparison.EQ;
		if (typeof comparator !== "object" || Object.keys(comparator).length !== 1) {
			throw new InsightError("MComparison must have exactly one key-value pair");
		}
		if (typeof Object.values(comparator)[0] !== "number") {
			throw new InsightError("The value for MComparison must be a number");
		}
	}

	private validateSComparison(sComparison: SComparison): void {
		const comparator = sComparison.IS;
		if (typeof comparator !== "object" || Object.keys(comparator).length !== 1) {
			throw new InsightError("SComparison must have exactly one key-value pair");
		}
		if (typeof Object.values(comparator)[0] !== "string") {
			throw new InsightError("The value for SComparison must be a string");
		}
	}

	public validateOptionsClause(options: Options): void {
		if (!options.COLUMNS || options.COLUMNS.length === 0) {
			throw new InsightError("COLUMNS must be a non-empty array");
		}

		options.COLUMNS.forEach((column) => {
			if (typeof column !== "string") {
				throw new InsightError("Each item in COLUMNS must be a string");
			}
		});

		// if (options.ORDER && typeof options.ORDER !== "string") {
		// 	console.log("--------------------------INSIDE VALIDATION--------------");
		// 	throw new InsightError("ORDER must be a string");
		// }
		if (options.ORDER) {
			const order = options.ORDER;
			if (typeof order === "string") {
				this.validateOrderString(order, options.COLUMNS);
			} else {
				this.validateOrderObject(order, options.COLUMNS);
			}
		}
	}

	private validateOrderString(order: string, columns: string[]): void {
		if (!columns.includes(order)) {
			throw new InsightError("ORDER must be one of the COLUMNS when it's a string");
		}
	}

	private validateOrderObject(order: Order, columns: string[]): void {
		if (order.dir !== "UP" && order.dir !== "DOWN") {
			throw new InsightError("ORDER direction must be UP or DOWN");
		}
		if (!Array.isArray(order.keys) || order.keys.length === 0) {
			throw new InsightError("ORDER keys must be a non-empty array");
		}
		order.keys.forEach((key) => {
			if (typeof key !== "string" || !columns.includes(key)) {
				throw new InsightError("Each ORDER key must be a string and included in COLUMNS");
			}
		});
	}

	private validateTransformationsClause(transformations: Transformations): void {
		this.validateGroupArray(transformations.GROUP);
		this.validateApplyArray(transformations.APPLY);
	}

	private validateGroupArray(group: string[]): void {
		group.forEach((key) => {
			if (typeof key !== "string") {
				throw new InsightError("Each GROUP key must be a string");
			}
		});
	}

	private validateApplyArray(apply: ApplyRule[]): void {
		const seenApplyKeys = new Set<string>();
		apply.forEach((rule) => {
			const applyKey = Object.keys(rule)[0];
			if (seenApplyKeys.has(applyKey)) {
				throw new InsightError(`Each APPLY key must be unique. Duplicate found: ${applyKey}`);
			}
			seenApplyKeys.add(applyKey);

			const applyToken = Object.keys(rule[applyKey])[0];
			const tokenValue = rule[applyKey][applyToken];
			this.validateApplyToken(applyToken, tokenValue);
		});
	}

	private validateApplyToken(applyToken: string, tokenValue: string): void {
		if (!["MAX", "MIN", "AVG", "COUNT", "SUM"].includes(applyToken)) {
			throw new InsightError(`Invalid APPLY token: ${applyToken}`);
		}
		if (typeof tokenValue !== "string") {
			throw new InsightError(`Value for ${applyToken} must be a string`);
		}
	}
}

import {
	Query,
	Filter,
	LogicComparison,
	MComparison,
	SComparison,
	Options,
	Negation,
	Order,
	Transformations, ApplyRule
} from "../dataModels/Query";
import CourseData from "../dataModels/CourseData";
import Section from "../dataModels/Section";
import {InsightError, InsightResult, ResultTooLargeError} from "../controller/IInsightFacade";
import Room from "../dataModels/Room";
import RoomData from "../dataModels/RoomData";
import Decimal from "decimal.js";

export class Executor {
	public extractDatasetId(query: Query): string {
		// Initialize an empty set to collect unique dataset IDs
		let datasetIds = new Set<string>();
		// Extract dataset IDs from the WHERE clause
		this.extractIdsFromFilter(query.WHERE, datasetIds);
		// Extract dataset IDs from the OPTIONS clause
		query.OPTIONS.COLUMNS.forEach((column) => {
			const datasetId = this.extractIdFromKey(column);
			if (datasetId) {
				datasetIds.add(datasetId);
			}
		});

		// Assuming consistency, there should only be one dataset ID
		if (datasetIds.size !== 1) {
			throw new Error("Query must use a single dataset ID");
		}

		return datasetIds.values().next().value;
	}

	// this helper method was created with the help of chatGPT
	private extractIdsFromFilter(filter: Filter, datasetIds: Set<string>): void {

		Object.entries(filter).forEach(([key, value]) => {
			if (key === "AND" || key === "OR") {
				(value as Filter[]).forEach((subFilter) => this.extractIdsFromFilter(subFilter, datasetIds));
			} else if (key === "NOT") {
				this.extractIdsFromFilter(value as Filter, datasetIds);
			} else {
				// MComparison, SComparison
				const fieldKey = Object.keys(value)[0];
				const datasetId = this.extractIdFromKey(fieldKey);
				if (datasetId) {
					datasetIds.add(datasetId);
				}
			}
		});
	}

	private extractIdFromKey(key: string): string | null {
		const underscoreIndex = key.indexOf("_");
		if (underscoreIndex !== -1) {
			return key.substring(0, underscoreIndex);
		}
		return null;
	}

	public applyFilters(courseData: CourseData | RoomData, filter: Filter): Section[] | Room[] {

		if (Object.keys(filter).length === 0) {
			if (courseData instanceof CourseData) {
				return courseData.sections;
			} else {
				return courseData.rooms;
			}
			// If the filter is empty, return all sections
		}

		if (courseData instanceof CourseData) {
			return courseData.sections.filter((section) => {
				return this.evaluateFilter(section, filter);
			});
		} else {
			return courseData.rooms.filter((section) => {
				return this.evaluateFilter(section, filter);
			});
		}
	}

	private isLogicComparison(filter: Filter): filter is LogicComparison {
		return "AND" in filter || "OR" in filter;
	}

	private isMComparison(filter: Filter): filter is MComparison {
		return "LT" in filter || "GT" in filter || "EQ" in filter;
	}

	private isSComparison(filter: Filter): filter is SComparison {
		return "IS" in filter;
	}

	private isNegation(filter: Filter): filter is Negation {
		return "NOT" in filter;
	}

	private evaluateFilter(section: Section | Room, filter: Filter): boolean {
		if (this.isLogicComparison(filter)) {
			if (filter.AND) {
				return filter.AND.every((subFilter) => this.evaluateFilter(section, subFilter));
			} else if (filter.OR) {
				return filter.OR.some((subFilter) => this.evaluateFilter(section, subFilter));
			} else {
				return false;
			}
		} else if (this.isNegation(filter)) {
			return !this.evaluateFilter(section, filter.NOT);
		} else if (this.isSComparison(filter)) {
			const [key, comparisonValue] = Object.entries(filter.IS)[0];
			const keyParts = key.split("_");
			const value = filter.IS[key];
			const getValue = section.get(keyParts[1]) as string;
			// Assuming wildcards are not to be implemented in this example
			if (comparisonValue.startsWith("*") && comparisonValue.endsWith("*")) {
				const trimmedValue = comparisonValue.slice(1, -1);
				return getValue.includes(trimmedValue);
			} else if (comparisonValue.startsWith("*")) {
				const trimmedValue = comparisonValue.slice(1);
				return getValue.endsWith(trimmedValue);
			} else if (comparisonValue.endsWith("*")) {
				const trimmedValue = comparisonValue.slice(0, -1);
				return getValue.startsWith(trimmedValue);
			} else {
				return getValue === value;
			}
		} else if (this.isMComparison(filter)) {
			const comparatorKey = Object.keys(filter)[0];
			const key = Object.keys((filter as any)[comparatorKey])[0];
			const targetValue = (filter as any)[comparatorKey][key];
			const keyParts = key.split("_");
			const sectionValue = section.get(keyParts[1]);

			if (sectionValue === undefined) {
				throw new Error("Attempted to compare an undefined value");
			}
			switch (comparatorKey) {
				case "LT": return sectionValue < targetValue;
				case "GT": return sectionValue > targetValue;
				case "EQ": return sectionValue === targetValue;
				default: throw new Error(`Unknown comparison: ${comparatorKey}`);
			}
		} else {
			throw new Error("Unknown filter type");
		}
	}

	// this helper method was created with the help of chatGPT
	public formatResults(sections: Array<Section | Room>, options: Options): any[] {
		const results = sections.map((section) => {
			const result: any = {};
			options.COLUMNS.forEach((column) => {
				const colParts = column.split("_");
				const key = colParts[1];
				if (colParts.length === 2) {
					if ((section instanceof Section) || (section instanceof Room)) {
						result[column] = section.get(key);
					} else {
						result[column] = section[column as keyof Section];
					}
				} else {
					// If there's no underscore, it's an applied column, so just directly access the property
					result[column] = (section as any)[column];
				}
			});
			return result;
		});


		// Add sort functionality if there is an ORDER clause
		if (options.ORDER) {
			results.sort(this.getSortFunction(options.ORDER));
		}
		return results;
	}

// Helper function to generate a sort function based on the ORDER clause
	private getSortFunction(order: Order | string): (a: any, b: any) => number {
		// Handle string ORDER, which is a single key with ascending sort
		if (typeof order === "string") {
			return (a, b) => {
				return a[order] > b[order] ? 1 : a[order] < b[order] ? -1 : 0;
			};
		}
		// Handle Order object, which can have multiple keys and a direction
		return (a, b) => {
			for (const key of order.keys) {
				if (a[key] !== b[key]) {
					const directionMultiplier = order.dir === "UP" ? 1 : -1;
					return a[key] > b[key] ? directionMultiplier : -directionMultiplier;
				}
			}
			return 0;
		};
	}

	public executeGroupAndApply(rows: Section[] | Room[], transformations: Transformations): any[] {
		// console.log("-----------------------------INSIDE GROUP AND APPLY-------------------------------------");
		const groups = this.groupBy(rows, transformations.GROUP);
		// console.log("-----------------------------GROUPBY DONE --------------------------------------");
		return this.applyTransformations(groups, transformations.APPLY, transformations.GROUP);
	}

	// this helper method was created with the help of chatGPT
	private groupBy(rows: Array<Section | Room>, groupKeys: string[]): Map<string, Array<Section | Room>> {
		const groups = new Map<string, Array<Section | Room>>();
		rows.forEach((row) => {
			const groupKey = groupKeys.map((key) => {
				const value = row.get(key.split("_")[1]);
				if (value === undefined) {
					throw new InsightError(`Value for key ${key} is undefined`);
				}
				return typeof value === "number" ? value.toString() : value;
			}).join("||");

			const group = groups.get(groupKey) || [];
			group.push(row);
			groups.set(groupKey, group);
		});
		return groups;
	}

	private applyTransformations(groups: Map<string, Array<Section | Room>>
		, applyRules: ApplyRule[], groupKeys: string[]): any[] {
		// console.log("-----------------------------TRANSFORMATION STARTS--------------------------------------");
		const transformedResults: any[] = [];
		// Iterate through each group to apply the transformations
		groups.forEach((groupRows, groupKey) => {
			// Initialize an object to accumulate results for this group
			const result: any = {};
			// Handle APPLY rules, performing aggregation operations on the groupRows
			applyRules.forEach((rule) => {
				const applyKey = Object.keys(rule)[0];
				const applyOperation = rule[applyKey];
				const operationType = Object.keys(applyOperation)[0];
				const fieldToOperateOn = applyOperation[operationType];
				result[applyKey] = this.performApplyOperation(groupRows, fieldToOperateOn, operationType);
			});
			// Split the concatenated groupKey to get individual group values
			// Add the group values to the result object using the keys from groupKeys
			const groupValues = groupKey.split("||");
			groupKeys.forEach((key, index) => {
				result[key] = groupValues[index];
			});
			transformedResults.push(result);
		});
		return transformedResults;
	}

	private performApplyOperation( groupRows: Array<Section | Room>, applyToken: string, targetField: string): number {
		if (targetField === "AVG") {
			return this.getAvg(groupRows, applyToken);
		} else if (targetField === "MAX") {
			return this.getMax(groupRows, applyToken);
		} else if (targetField === "MIN") {
			return this.getMin(groupRows, applyToken);
		} else if (targetField === "SUM") {
			return this.getSum(groupRows, applyToken);
		} else if (targetField === "COUNT") {
			return this.getCount(groupRows, applyToken);
		} else {
			throw new InsightError("Invalid");
		}
	}

	private getMax(groupRows: Array<Section | Room>, targetField: string): number {
		return groupRows.reduce((max, row) => {
			const value = row.get(targetField.split("_")[1]);
			return typeof value === "number" && value > max ? value : max;
		}, Number.NEGATIVE_INFINITY);
	}


	private getMin(groupRows: Array<Section | Room>, targetField: string): number {
		return groupRows.reduce((min, row) => {
			const value = row.get(targetField.split("_")[1]);
			return typeof value === "number" && value < min ? value : min;
		}, Number.POSITIVE_INFINITY);
	}

	private getAvg(groupRows: Array<Section | Room>, targetField: string): number {
		let total = new Decimal(0);
		groupRows.forEach((row) => {
			const value = row.get(targetField.split("_")[1]);
			if (typeof value === "number") {
				total = total.add(new Decimal(value));
			}
		});
		if (groupRows.length === 0) {
			throw new InsightError("No valid rows for average calculation");
		}
		const avg = total.toNumber() / (groupRows.length);
		return Number(avg.toFixed(2));
	}

	private getSum(groupRows: Array<Section | Room>, targetField: string): number {
		let total = 0;
		groupRows.forEach((row) => {
			const value = row.get(targetField.split("_")[1]);
			if (typeof value === "number") {
				total += value;
			}
		});
		return Number(total.toFixed(2));
	}

	private getCount(groupRows: Array<Section | Room>, targetField: string): number {
		const uniqueValues = new Set();
		groupRows.forEach((row) => {
			const value = row.get(targetField.split("_")[1]);
			if (value !== undefined) {
				uniqueValues.add(value);
			}
		});
		return uniqueValues.size;
	}

	public sortResults(results: any[], order: Order): any[] {
		const directionMultiplier = order.dir === "UP" ? 1 : -1;

		// Sort results by the specified keys
		return results.sort((a, b) => {
			for (const key of order.keys) {
				if (a[key] < b[key]) {
					return -1 * directionMultiplier;
				} else if (a[key] > b[key]) {
					return 1 * directionMultiplier;
				}
				// if a[key] === b[key], continue to the next key
			}
			return 0; // If all keys are equal
		});
	}
}

